INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1153, 'Software Developer - DevOps at Esri (Redlands, CA)', '&lt;p&gt;Work closely with a talented team of dynamic and passionate engineers to help our customers make a difference. In this role, you&amp;rsquo;ll design, build, and operate ArcGIS Online, Esri&amp;rsquo;s rapidly growing next generation cloud-based mapping platform. ArcGIS Online is used by thousands of organizations around the world to map disasters, maintain infrastructure, manage natural resources, and more.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position is available at Esri headquarters in Redlands, CA and optionally at our R&amp;amp;D Center in Washington D.C.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and implement operational architecture for a variety of applications and multi-tier services running on web, compute, and database tiers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Manage operational infrastructure used for ArcGIS Online&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build deployment automation scripts and manage deployments&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Manage&amp;nbsp;continuous&amp;nbsp;integration software build systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and implement monitoring systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Respond to alerts; troubleshoot problems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help develop and extend the ArcGIS Online platform&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Redlands, CA', 'http://careers.stackoverflow.com/jobs/77662/software-developer-devops-esri', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1120);
