INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1154, 'Digital Developer - Front End Specialist at Bauer Xcel Media (Bauer Media Group) (Sydney, Australia)', '&lt;p&gt;&lt;strong&gt;BAUER XCEL MEDIA&lt;/strong&gt; is Bauer Media''s Digital Division, advancing the media industry by applying user driven strategies to deliver cutting edge news and build original, innovative products centred around audience needs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Work for a large digital media company with offices in New York, Hamburg, London, Sydney, Melbourne, Aukland and Warsaw, you will work on high profile brands with great content to create exciting new digital products in a network that reaches and impacts millions of people.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Who you are:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;know how to build performant, accessible mobile first responsive web sites&lt;/li&gt;&lt;br /&gt;&lt;li&gt;have experience working with Node and a deep understanding of client side web technologies including video&lt;/li&gt;&lt;br /&gt;&lt;li&gt;experience working with analytics and ad placement technologies and care about collecting relevant data to help improve product and UX&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;As part of the development team you will:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;build highly robust and scalable web sites&lt;/li&gt;&lt;br /&gt;&lt;li&gt;shape and implement mobile first responsive designs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;collaborate closely and become a valued contributor in an autonomous cross-functional team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;work in a convenient, lively Sydney CBD location&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Sydney, Australia', 'http://careers.stackoverflow.com/jobs/99696/digital-developer-front-end-specialist-bauer-xcel-media-bauer-media', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02