INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1163, 'Software Developer - JavaScript/HTML5 at Esri (Redlands, CA)', '&lt;p&gt;Are you excited about web applications? Join our high-energy, visionary ArcGIS product development team and help design the next generation of web apps for the ArcGIS platform. ArcGIS is available to customers&amp;nbsp;online by subscription at &lt;a href="http://www.arcgis.com/" rel="nofollow" rel="nofollow" rel="nofollow"&gt;arcgis.com&lt;/a&gt;&amp;nbsp;as well as On-Premises as installable server software.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Envision, design, and develop web applications&amp;nbsp;and UI modules for large-scale deployment and management&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Rapidly prototype web applications to showcase new concepts and ideas&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Drive the REST API design, required for integrating the web application with the back end&amp;nbsp;web services&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create a rich developer-friendly toolkit to consume new server-side GIS functionalities, using the latest relevant techniques and best practices&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Apply good software design and development practices in daily activities&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write comprehensive technical design specifications and support documentation&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Redlands, CA', 'http://careers.stackoverflow.com/jobs/77661/software-developer-javascript-html5-esri', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1120);
