INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1167, 'Android Engineer at Vokal (Chicago, IL)', '&lt;p&gt;If you are an Android application developer who loves to solve problems and gets excited about agile process for Android application development, we can&amp;rsquo;t wait to have you on board.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position is based in our home office, located in Chicago&amp;rsquo;s River North neighborhood.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We also offer a competitive compensation package.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Position Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Contribute best-in-class programming skills to develop highly innovative, consumer-facing, and enterprise mobile solutions&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Support and collaborate with clients, other developers, and designers to determine business and technical solutions&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Continually deliver creative solutions with awesome execution&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/94069/android-engineer-vokal', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1167);
