INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1171, 'Cloud Architect at Booz Allen Hamilton (Rockville, MD)', '&lt;p&gt;&lt;strong&gt;Key Role:&lt;br&gt;&lt;/strong&gt;Work with business analysts and software architects to design Cloud-based software solutions and identify and execute effective application migration strategies. Lead the development of Cloud native software solutions and the development team in planning iterations, user stories, and development sprints. Lead the developers in building leading edge, perform software solutions based upon the latest design patterns, best practices, and technology stacks. Troubleshoot performance and design issues. Identify ways to optimize development and operations after migration and champion DevOps culture, where appropriate. Communicate designs and technical solutions effectively to internal and external stakeholders. Research and pilot new Cloud technologies, trends, and innovative solutions to apply to the client space&lt;/p&gt;', 'Rockville, MD', 'http://careers.stackoverflow.com/jobs/99691/cloud-architect-booz-allen-hamilton', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1164);
