INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1173, 'Developer Advocate at Esri (Redlands, CA)', '&lt;p&gt;Are you an energetic, passionate software engineer who has the desire to inspire other developers? Join our product development team and evangelize Esri&amp;rsquo;s newest technologies. We are looking for exceptional technology gurus who can educate and enable our customers to be innovative and achieve success.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&amp;nbsp; &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work with Esri&amp;rsquo;s global community of users to understand their needs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with Esri developers to further expand our products and help to define the product roadmap&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design APIs for new features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build example applications and samples using the latest platform technologies and features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write compelling user documentation, tutorials, articles, and blog posts&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Present and evangelize our products in the user community through presentations, hackathons, webinars, forums, blogs, and social media&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Keep up to date with current technology and trends&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Redlands, CA', 'http://careers.stackoverflow.com/jobs/77653/developer-advocate-esri', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1120);
