INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1175, 'Mobile Software Engineer - Android at PostBeyond (Toronto, ON, Canada)', '&lt;p&gt;PostBeyond is seeking a top-notch, self-directed Android Software Engineer with a desire to build innovative and exciting mobile applications. The Engineering Team at PostBeyond is responsible for our Advocacy platform that delivers a personalized content engine for groups of advocates. In this role, you will work with a team of engineers to design and develop our proprietary app. We&amp;rsquo;ve launched a modern platform that makes it easy for us to experiment, test and ship new features. Ideal candidates will be detail-oriented and passionate about design, user experience, performance and usability.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You are creative Android Developer and you will support and develop PostBeyond for Android using the Android SDK. You will also be involved in development of the PostBeyond SDK library and various additional components as well as develop internal projects that might be released as part of contribution to the Open Source community.&lt;/p&gt;', 'Toronto, ON, Canada', 'http://careers.stackoverflow.com/jobs/99689/mobile-software-engineer-android-postbeyond', 'Tue, 06 Oct 2015 21:02:02 Z', '2015-10-06T21:02:02Z', 1175);
