INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1177, 'Hosting Operations Engineer at Blackboard (State College, PA)', '&lt;p&gt;Blackboard helps shape the future of education by delivering technology and services solutions that transform the learning experience.&amp;nbsp; We continually innovate to better enable educators, parents, and learners of all types to teach, learn, connect and communicate whenever they want and wherever they are.&amp;nbsp; We partner with educational institutions globally in the higher education, K-12 and professional education markets to improve the education experience both inside and outside the classroom by facilitating access to information, enabling stronger student engagement and collaboration, enhancing communication, and supporting student safety, success and institutional effectiveness.&amp;nbsp; Most importantly, we help every student achieve their full potential through access to lifelong learning.&lt;br&gt;&lt;br&gt;&lt;strong&gt;Position Responsibilities&lt;/strong&gt;&lt;br&gt;&lt;br&gt;The Development Operations Engineer will play an integral role in the success of the Platform Delivery group.&amp;nbsp; Specifically s/he will be responsible for:&lt;br&gt;&lt;br&gt;&lt;strong&gt;&lt;em&gt;Hosting &amp;amp; Service Deployment&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Monitoring and maintenance performance of co-location hosting equipment including DDoS mitigation devices, firewalls, load balancers, virtual host servers, storage devices, and physical servers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Configuring servers and other networking equipment.&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintaining security and backup for delivered services.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assisting with product site creation, activation&amp;nbsp;and maintenance.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assisting with product server installation and deployment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintaining hosting guidelines and&amp;nbsp;standards.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assisting in database maintenance and warehousing functions.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Applying necessary system updates to product servers.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;Client Services&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Assisting with installation, and support of network and workstation hardware, software, and physical connections at client sites.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Acting as backup for troubleshooting services for clients as needed.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'State College, PA', 'http://careers.stackoverflow.com/jobs/99688/hosting-operations-engineer-blackboard', 'Tue, 06 Oct 2015 21:02:02 Z', '2015-10-06T21:02:02Z', 1177);
