INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1182, 'Java Developer at Remedy Partners Inc. (New York, NY)', '&lt;p&gt;Remedy Partners is seeking an &lt;strong&gt;Enterprise Back End Java Web Developer&lt;/strong&gt;; a Software Engineer specializing in Enterprise architecture and Web technologies to help us build the Episode Connect platform.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, develop, and test high performance, scalable, highly available, secure multi-tier web-based solutions;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Troubleshoot issues with code, web server, database, security, network, and web technologies;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design solutions and integrate with third-party systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Defines site objectives by analyzing user requirements; envisioning system features and functionality.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Integrates applications by designing database architecture and server scripting; studying and establishing connectivity with network systems, search engines, and information servers.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Supports users by developing documentation and assistance tools.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Apply best development practices and mentor junior team members.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Desired Skills &amp;amp; Experience:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Bachelors in Computer Science or related field or equivalent work experience;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extensive hands on experience (5+ years) software engineering;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extensive experience coding in Java/J2EE and/or Core Java (3+ years) and/or other OO languages;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience in building Enterprise Web architectures&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with distributed systems: Camel, Message Queues.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extensive experience with Web technologies and tools;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience designing and building scalable, fault tolerant large scale distributed systems;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extensive experience with Object Oriented Programming;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Solid engineering background with knowledge of code and system performance methodologies;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Extensive debugging and problem solving skills; proven ability to identify and fix root causes;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong test driven development background;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Excellent written and verbal communication skills, with the ability to work well with a team;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Practitioner of full software development lifecycle including requirements gathering, specification, documentation, testing, release, and maintenance;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proficient in the following:&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Core Java/J2EE, Spring framework, Hibernate, Rules Engine, Workflow Engines, Camel, Message Queues: Kafka, Enterprise Design Patterns, Design Patterns.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;Plus:&lt;br&gt; NoSQL/RDMB experience&lt;br&gt; Experience in HealthCare and HealthCare Standards (HL7,CCD,X12)&lt;br&gt; &lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99686/java-developer-remedy-partners-inc', 'Tue, 06 Oct 2015 21:02:02 Z', '2015-10-06T21:02:02Z', 1182);
