INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1186, 'System Software Developer at AppFirst (Bloomington, MN)', '&lt;p&gt;&lt;span&gt;We are looking for a systems software developer with a passion for OS internals. This is NOT a web application developer role. This position is located in Minneapolis, Minnesota&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;The ideal candidate will have a thirst for knowledge and curiosity about operating system design and implementation. In this position, you will be working with our unique intellectual property that allows us to create the most extensive data set ever formulated for describing the operation of production applications. If you are motivated by a challenge and interested in solving hard problems, we want you.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We have created a place where people who have a passion for writing code are able to thrive. If your passion is writing code, without all the meetings and disruptions, then this could be a place where you can do your best work. &lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you&amp;rsquo;ll be doing:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work on our Windows Collector to gather data about applications from *inside* the process.&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Write rigorous tests to ensure functionality of applications while gathering this data.&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Work closely with others in an interactive development environment.&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Bloomington, MN', 'http://careers.stackoverflow.com/jobs/99684/system-software-developer-appfirst', 'Tue, 06 Oct 2015 21:02:02 Z', '2015-10-06T21:02:02Z', 1186);
