INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1218, 'Python Developer at Aruba Networks (Portland, OR)', '&lt;p&gt;&lt;strong&gt;Job Role: &lt;/strong&gt;Python Developer&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Location: &lt;/strong&gt;Portland, OR&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;The Team You Will Join&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;a href="http://meridianapps.com/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Meridian&lt;/a&gt; is an Aruba Networks company that helps places around the world build amazing mobile apps. You''ll experience the Meridian platform in venues like the new 49ers Stadium in SF, the American Museum of Natural History in NYC, and the Marina Bay Sands resort in Singapore. &amp;nbsp;Check out what the Meridian team does:&amp;nbsp; &lt;a href="http://meridianapps.com/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;http://meridianapps.com/&lt;/a&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Who We Are Looking For&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are currently seeking a talented, backend focused Python developer to join our team in Portland''s Downtown Waterfront. You will be helping to build and refine our web-based management and design tools used by customers to build amazing location-based mobile apps.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We build products in Python using Django running on App Engine. That said, we value knowledge and capability more than experience in any specific technology. More important than the tools you choose is your talent for using them&amp;mdash;your instincts for guiding the design and structure of a large Python application. Basically, if you enjoy building ambitious applications for the web in any form, we''d like to chat with you.&lt;/p&gt;', 'Portland, OR', 'http://careers.stackoverflow.com/jobs/99676/python-developer-aruba-networks', 'Tue, 06 Oct 2015 20:02:02 Z', '2015-10-06T20:02:02Z', 1169);
