INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1230, 'Mid/Sr. Fullstack Developer (NODE/Python/Java/Scala) at TIME, Inc. (CTO Team) (New York, NY)', '&lt;p&gt;&lt;strong&gt;TIME, Inc. - Rapid Applications Development Team&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;*a start-up like culture/team within TIME, Inc.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Fullstack Engineer -&amp;nbsp;Mid/Sr. Level - 100% Hands-on Coding Role, although Leadership is a possibility.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;TIME''s Rapid Applications Development Team&lt;/strong&gt; reports directly to the CTO and handles 90% of TIME''s new Mobile Platforms and Greenfield Initiatives. The team is headed by ex-Amazon Principal Engineers and they''re looking to add awesome Fullstack Engineers on to their team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Location&lt;/strong&gt; - NYC (Offices will eventually be in Midtown, Brooklyn, and Downtown FiDi)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;See required skills below:&lt;/strong&gt;&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/99670/mid-sr-fullstack-developer-node-python-java-time-inc-cto-team', 'Tue, 06 Oct 2015 19:02:02 Z', '2015-10-06T19:02:02Z', 1230);
