INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1232, 'Principal Software Engineer / Architect - CLOUD at Exa Corporation (Burlington, MA)', '&lt;p&gt;Exa Corporation seeks a Principal Software Engineer / Architect with broad experience in the development of software products and web applications, with an emphasis on Enterprise Java.&amp;nbsp; You will be a key member of the ExaCLOUD product development team. ExaCLOUD&amp;trade; provides secure, web-based, access to the complete Exa simulation suite anywhere, anytime. It is a multi-user system that enables engineers to easily and intuitively manage simulation projects, to collaborate seamlessly with colleagues, and use any of Exa&amp;rsquo;s powerful simulation preparation or results analysis tools with just a web browser.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Role &amp;amp; Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are focused on adding new features and enhancements to the ExaCLOUD&amp;trade; product. In this newly created role, you will contribute your ideas and expertise, and have responsibility to:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, Architect and develop features and enhancements of ExaCLOUD Product.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide critical architectural input in key decisions.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide technical leadership and mentoring to other engineers on the team.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with cross-functional and geographically distributed teams.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Burlington, MA', 'http://careers.stackoverflow.com/jobs/98301/principal-software-engineer-architect-cloud-exa-corporation', 'Tue, 06 Oct 2015 19:02:02 Z', '2015-10-06T19:02:02Z', 1232);
