INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1244, 'Senior Web Designer at Chatham Financial (Kennett Square, PA)', '&lt;p&gt;We are seeking a designer to join our growing product design team. You&amp;rsquo;ll produce user-system&amp;nbsp; workflows, product prototypes, and interface mockups while maintaining usability standards and industry best practices.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a designer at Chatham Financial, you&amp;rsquo;ll recognize that great design balances aesthetics with empathy and understanding for users and their needs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a member of our design team you will&amp;hellip;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Demonstrate strong collaboration skills as you work with our product management, subject matter experts, marketing, and engineering teams.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build and analyze use cases and translate them into efficient workflows.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create storyboards, prototypes, and interface mockups to demonstrate product design ideas.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Establish design patterns as well as be an ambassador for best practices and usability standards across our suite of products.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be able to effectively communicate, present, and defend design concepts to stakeholders, and analyze and implement feedback with an open mind.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Kennett Square, PA', 'http://careers.stackoverflow.com/jobs/99664/senior-web-designer-chatham-financial', 'Tue, 06 Oct 2015 19:02:02 Z', '2015-10-06T19:02:02Z', 1244);
