INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1251, 'Lead Full Stack Developer at CheckedUp (New York, NY)', '&lt;p&gt;&lt;span&gt;CheckedUp is looking for a senior software engineer to lead the development of a new product in the field of Digital Health. The position is for a Python and Django expert with experience on AWS.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We''re looking for an experienced Full stack web developer that is an expert on python server-side development technologies including content management and delivery, analytics, API design, security and encryption and database design, who is excited about contributing to the conception and development of a new product and who is enthusiastic and team-oriented.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;You would be integral in contributing to the future direction of the company and product from a technical perspective by deciding what challenges to tackle, what platforms to embrace, what technologies to employ, and generally how to best improve the value of the product with an eye toward quality, stability and real-world utility.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;You would be responsible for the planning, architecture, development and maintenance of the existing application''s Full Stack (mobile or web) and backend; designing and implementing a RESTful interface for mobile and other clients; developing and/or managing the maintenance and development of current and future mobile applications; working closely with the product and design teams to create a beautiful and technically sound application.&lt;/span&gt;&lt;/p&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/79386/lead-full-stack-developer-checkedup', 'Tue, 06 Oct 2015 18:02:02 Z', '2015-10-06T18:02:02Z', 1251);
