INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1266, 'Senior Web-Applications Engineer at American Economic Association (Pittsburgh, PA)', '&lt;p&gt;Position available for a talented web-applications developer who would like to be involved in all project phases&amp;mdash;everything from assessment and requirements analysis through design, coding, maintenance, and upgrades. &amp;nbsp;&amp;nbsp;This opportunity is perfect for a person who loves to collaborate in a very small group setting. &amp;nbsp;It allows for a large degree of control, creativity, and growth.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for a LAMP-stack developer to build on key business applications, including memberships, subscriptions, and conference registration.New projects include the UI for the production of our EconLit database, which is sold to academic and business libraries worldwide.&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The American Economic Association publications office provides a warm, professional, relaxed environment. If you want to jump in, work hard, and have fun while you''re at it, this is a good place to be!&lt;/p&gt;', 'Pittsburgh, PA', 'http://careers.stackoverflow.com/jobs/99658/senior-web-applications-engineer-american-economic-association', 'Tue, 06 Oct 2015 17:02:02 Z', '2015-10-06T17:02:02Z', 1266);
