INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1275, 'Software/System Engineer at Stratocore (Paris, France)', '&lt;p&gt;The European Software Engineer position requires broad stroke competency, providing front-line and back-end technical support to our prospective and established clients at pre and post sales levels at an international level&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;strong&gt; Technical Support: &lt;/strong&gt;Technical support requests, Client site implementation, Infrastructure maintenance and scaling&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;strong&gt; Development: &lt;/strong&gt;API/Scripting for integration. Programing to generate reports and views. Work directly with software development team planning, designing and creating functionality&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;strong&gt; Coordination: &lt;/strong&gt;Participate in high-level client contact. Help ensure implementation processes, provide high-level IT software trainings; coordinate with client IT tech teams and Stratocore IT teams. Work directly with software development team.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Paris, France', 'http://careers.stackoverflow.com/jobs/94925/software-system-engineer-stratocore', 'Tue, 06 Oct 2015 17:02:02 Z', '2015-10-06T17:02:02Z', 1275);
