INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1281, 'Full Stack Software Engineer ( React.js, Rails, and more) at GoCo.io (Houston, TX)', '&lt;p&gt;Work directly with our CTO and awesome development team using state of the art technologies. Our tech stack is React.js with ES6/ES7 in a Single Page App coupled with a Ruby on Rails REST API. We''ve got an awesome, close-knit team and a super fun tech startup culture. This is a full stack coding position with massive opportunities for leadership and career advancement in a rapidly growing company. You will be encouraged and hopefully excited to wear many hats, including shaping the product vision, giving input on User Experience, vetting new technologies, refining our software architecture, and helping with hiring.&lt;/p&gt;', 'Houston, TX', 'http://careers.stackoverflow.com/jobs/98089/full-stack-software-engineer-reactjs-rails-gocoio', 'Tue, 06 Oct 2015 17:02:02 Z', '2015-10-06T17:02:02Z', 1281);
