INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1283, 'Front-End Developer at ClickTripz (Manhattan Beach, CA) (allows remote)', '&lt;p&gt;ClickTripz is looking for a talented front-end developer. This is a full-time position and the developer would be able to work remotely while communicating with our office in Manhattan Beach, California.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The ideal candidate is a motivated self-starter who can work creating flexible solutions that reach the goals and business needs of our customers. This person must be comfortable assuming ownership of projects and be able to adapt to changing requirements.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Integrate the ClickTripz product into our partner software.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain on-going partner integrations.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Handle calls with partners regarding integrations.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Trouble-shoot any issues regarding front-end solutions, think creatively on how to implement solutions.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Implement new front-end features for the ClickTripz product.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Manhattan Beach, CA', 'http://careers.stackoverflow.com/jobs/82881/front-end-developer-clicktripz', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1283);
