INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1287, 'Softwareentwickler fr Java &amp; Mobile (m/w) at Triona GmbH (Mainz, Deutschland)', '&lt;p&gt;Wir suchen gestandene Java Enterprise und Mobile Apps Entwickler oder gern auch Menschen mit Talent und Begeisterung, die nach Ihrem Informatik-Studium Java Enterprise- und Mobile-Anwendungen entwickeln m&amp;ouml;chten.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Als Teil unseres Entwicklerteams unterst&amp;uuml;tzen Sie uns bei der Realisierung von IT-Projekten in Mainz oder bei unseren Kunden vorwiegend im Rhein/Main-Gebiet. Nach Ihrer ausf&amp;uuml;hrlichen und individuell zugeschnittenen fachlichen Weiterbildung in unserem Hause werden wir Sie - je nach Schwerpunkt und Erfahrung - mit der Konzeption, dem Design oder der Programmierung von Software haupts&amp;auml;chlich im Bereich Finanz und Logistik betrauen.&lt;/p&gt;', 'Mainz, Deutschland', 'http://careers.stackoverflow.com/jobs/99647/softwareentwickler-f%C3%BCr-java-mobile-m-w-triona-gmbh', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1287);
