INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1289, 'Lead Software Engineer at iLoan, a Springleaf brand (Chicago, IL)', '&lt;p&gt;We are looking for a Lead&amp;nbsp;Software&amp;nbsp;Engineer&amp;nbsp;to join our Digital team in Chicago. You''ll work as part of a full-stack agile development team to design and implement web applications.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Joining our office of 45+ current employees gives you the opportunity to build new, cutting-edge online lending platforms. As a new division&amp;nbsp;within the larger Springleaf organization, we have the fun, fast-paced, and collaborative environment of a startup without the small-company risk. Become a part of our innovative team in an exciting, high-growth industry.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The ideal candidate understands how to balance a desire for perfectionism with the need for pragmatism. You will integrate with and leverage our existing systems as you simultaneously help build longer lasting and more scalable solutions from scratch.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we will want you to do:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Build RESTful APIs &amp;amp; customer facing web applications&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write automated tests &amp;amp; ensure test coverage&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Conduct code reviews for your team&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/94922/lead-software-engineer-iloan-a-springleaf-brand', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1289);
