INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1291, 'Sr. Software Engineer in Test at iLoan, a Springleaf brand (Chicago, IL)', '&lt;p&gt;We are looking for a Senior Software Engineer in Test to join our Digital team, contributing to the success of our web presence through Springleaf.com and our new online lending platform, iLoan.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The ideal candidate will be highly motivated, reliable, detailed, and driven to produce high quality work. This individual must thrive in a fast-paced environment.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Work closely with developers to delivery stories in an agile environment&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Analyzing business requirements and creating thorough test plans&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Review code changes and unit tests and leverage knowledge to focus your testing&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Investigate bugs and fix or provide root cause analysis&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Initiative and drive new automation initiatives&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Create and maintain automated tests based on user stories&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Mentor other team members on automated testing&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; Manage the continuous integration process and builds&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/94921/sr-software-engineer-in-test-iloan-a-springleaf-brand', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1289);
