INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1293, 'Software Engineer - C# at Lucidea (Vancouver, BC, Canada)', '&lt;p&gt;&lt;strong&gt;Software Engineer &amp;ndash; C#&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Lucidea is a global provider of content and information management software and services. Due to continued growth, we have a couple of new openings for Software Engineers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Essence of Position&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As the successful candidate, you are a flexible, innovative and detail oriented problem solver, and will play a key role on our R&amp;amp;D team designing and developing leading edge software products.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Work and collaborate within a team of software engineers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design, develop, test, and support highly flexible database driven software solutions utilizing the latest .NET technologies&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;', 'Vancouver, BC, Canada', 'http://careers.stackoverflow.com/jobs/99649/software-engineer-c-sharp-lucidea', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1293);
