INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1299, 'Data Engineer at MassMutual Financial Group (Boston, MA)', '&lt;p&gt;We are seeking an exceptional data engineer to build and maintain big data infrastructure. This infrastructure supports the development of a strategic data asset and the execution of data science projects, which include complex applications of machine learning, visualization, and data mining. This is an opportunity to join a growing team of talented individuals, with diverse backgrounds in applied math, computer science and physics, focused on using novel big data technology and data science to answer fundamental questions that directly impact the direction of the company and industry at large.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Essential Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, build and measure data ingestion and integration pipelines for large volumes of temporal data from different sources. Examples include database extracts, application server logs, scanned images, voice recordings, Twitter streams, websites, and health sensor data.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design, build, and measure complex ETL jobs to process disparate, dirty data sources and form a high integrity, high quality, clean data asset.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support and scale big data infrastructure (Cloudera CDH 5, MongoDB, and HP Vertica)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Monitor and track data quality and data flow dynamics&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and build web based APIs to facilitate easy access to data&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Boston, MA', 'http://careers.stackoverflow.com/jobs/97871/data-engineer-massmutual-financial-group', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1299);
