INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1309, 'Information Security Engineer  Ethical Hacker at Time Inc. (New York, NY)', '&lt;p&gt;&lt;strong&gt;The Job&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The Information Security Engineer (Application Security) will join a team of other Information Security professionals reporting directly to the Global Manager - Application Security. The position will have primary responsibility for managing security architecture reviews, vulnerability assessments and penetration testing. The Information Security Engineer will be part of a team providing state-of-the-art technical expertise and support to clients, IT management and staff in risk assessment and the implementation of appropriate data security procedures and products.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;You Will:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Collaborate&amp;nbsp;with the Information Security staff in the evaluation of risks and threats, development, implementation, communication, operation, monitoring and maintenance of the IT security policies and procedures which promote a secure and uninterrupted operation of all IT systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Identify&amp;nbsp;emerging vulnerabilities, evaluates associated risks and threats and provides countermeasures where necessary&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Monitor developments within application security and ensure internal policies, procedures, tools and awareness programs reflect current security methods such as those published by OWASP, WASC, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Serve as a point of contact with business clients and consult with infrastructure and software development teams to design proper security architecture&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Act as security liaisons with software developers globally&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Conduct&amp;nbsp;security architecture reviews for new development (web, digital, mobile) and modifications to existing ones&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Conduct&amp;nbsp;internal and oversee external penetration tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design&amp;nbsp;and implement Secure SDLC program (policies, methodologies and tools) across the company globally&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Execute&amp;nbsp;formalized Information Security programs and processes, such as vulnerability management, event monitoring, data loss prevention, and incident response&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Evaluate, implement and support information security related technology&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Respond to information security alerts and analyze events for appropriate action&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Conduct security awareness training for software developers&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/94911/information-security-engineer-ethical-hacker-time-inc', 'Tue, 06 Oct 2015 16:02:02 Z', '2015-10-06T16:02:02Z', 1303);
