INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1314, 'Lead C# Developer at Live on Demand (Amsterdam, Netherlands)', '&lt;p&gt;We are looking for a Lead C# Developer&amp;nbsp;who is responsible for the technical roadmap and product strategy of Live on Demand.&amp;nbsp;You are part of building a platform that is changing the way events are organized.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities &amp;nbsp;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Set the technical roadmap for the future and co-create the product strategy together with the founders.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Lead the dev team and help developers to grow both on a technical and personal level.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Building a scalable and modular system, whilst maintaining the existing code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Enhance and optimize code and database to ensure stability and longevity of the system.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Keep the speed in the Build-Measure-Learn cycle.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Connecting Third Party API&amp;rsquo;s.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Here&amp;rsquo;s what we have in mind for you&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Working with a small &amp;amp; great team of conquerers.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be part of an international rollout and play a leading role in the process.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A basic fixed income plus a variable bonus based on results.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;If results consistently exceed our expectations you will be invited to join a stock option plan.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;How about it?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you feel like having a coffee with one of the founders to discuss the opportunities of working with a great startup to rollout a game changing way of event organizing, do contact us via Jitske van de Worp,&amp;nbsp;jitske@liveondemand.com!&amp;nbsp;&lt;/p&gt;', 'Amsterdam, Netherlands', 'http://careers.stackoverflow.com/jobs/99618/lead-c-sharp-developer-live-on-demand', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1314);
