INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1318, 'Lead Applications and System Developer at HIGHFLEET (Baltimore, MD) (allows remote)', '&lt;p&gt;As Lead Applications and System Developer, you will:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Be the main technical bridge between customers and the development team, and a conduit for technical expertise.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Lead in client engagements, staying current on services project goals, how the services team expects to achieve those goals, and what the time limits are.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Stay current on new advances in the field, and on new advances in HIGHFLEET''s own software stack&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Provide insight to the development team about customer needs&amp;nbsp;and participate in core software changes.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Design and build solution architectures and the best uses of the HIGHFLEET system in an engagement.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Monitor and profile database usage, and general operating system issues.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Familiarity with particular technologies is less important than a solid computer science background and the ability to learn new languages and skills quickly.&lt;/p&gt;', 'Baltimore, MD', 'http://careers.stackoverflow.com/jobs/99645/lead-applications-and-system-developer-highfleet', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1318);
