INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1323, 'Software Developer at Geneva Trading (Chicago, IL)', '&lt;p&gt;The Software Developer will join Geneva''s Algorithmic Trading team to work on various components of trading, trade support and research tools.&amp;nbsp; Initially the developer will be assisting in maintaining and enhancing existing tools, mainly in C# and C++.&amp;nbsp; There is opportunity for this individual to master and take ownership of some modules/areas and grow into a senior role within the team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Professional experience is not compulsory for this role as long as the candidate can demonstrate the&amp;nbsp;basic understanding of a majority of the skills below and the ability to&amp;nbsp;learn quickly.&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99644/software-developer-geneva-trading', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1323);
