INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1328, 'C#.NET/SQL Developer - Partial Telecommute at CheckPointHR (Edison, NJ)', '&lt;p&gt;As a&amp;nbsp;&lt;strong&gt;Software Developer&lt;/strong&gt; you will&amp;nbsp;be responsible&amp;nbsp;for full life cycle development of a variety of critical&amp;nbsp;projects for our flagship products to include our Payroll solution, Comprehensive Benefits solution or&amp;nbsp;helping to automate our back office operations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will have&amp;nbsp;the opportunity to work directly with clients and internal stakeholders to understand/develop business requirements, develop detailed systems specifications, design/develop complex application features and develop and execute unit testing. &amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This can be a partial telecommute position; you will have the opportunity to work from home 2x/week.&lt;/p&gt;', 'Edison, NJ', 'http://careers.stackoverflow.com/jobs/99613/c-sharpnet-sql-developer-partial-telecommute-checkpointhr', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1328);
