INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1334, 'Senior Mobile Software Engineer at comScore (Amsterdam-Zuidoost, Netherlands)', '&lt;p&gt;As a mobile developer, you''ll be responsible for creating and expanding our native and JavaScript SDKs and libraries for application and streaming analytics.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be working with algorithms on multiple platforms, including phones, tablets and other internet-connected devices, as well as upcoming hardware (Apple Watch, Google Glass, etc)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You should be experienced in mobile app development, coupled with in-depth knowledge of JavaScript and other web-technologies.&amp;nbsp;Close collaboration with the local and American reporting team and QA engineers will also be an essential part of your work.&lt;br&gt;&lt;br&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We''re a close knit international team of experienced Engineers who are passionate about web and application analytics and finding innovative ways to provide analytics data on the latest technology to solve the needs of our clients. &amp;nbsp;We&amp;rsquo;re looking for like-minded developers who are keen to solve interesting problems.&lt;/p&gt;', 'Amsterdam-Zuidoost, Netherlands', 'http://careers.stackoverflow.com/jobs/82585/senior-mobile-software-engineer-comscore', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1334);
