INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1336, 'SQL Server Developer - Healthcare IT Product at Medisolv Inc (Columbia, MD)', '&lt;p&gt;Medisolv, Inc. is a national leader in EHR-based quality measurement and compliance reporting. Our clinical, quality and business intelligence reporting for hospitals and health care systems provides a comprehensive solution for quality management and patient safety. Poised at the forefront of the industry, Medisolv employs the brightest minds to create and guide the direction of our solutions today and into the future.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We have an immediate need for a SQL Server Developer. The candidate should have experience working with MS SQL Server, SQL Server 2008/2012.&lt;/p&gt;', 'Columbia, MD', 'http://careers.stackoverflow.com/jobs/94908/sql-server-developer-healthcare-it-product-medisolv-inc', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1336);
