INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1343, 'Informatics Support Engineer at Apervita, Inc. (Chicago, IL)', '&lt;p&gt;The Informatics Support Engineer will be responsible for working with informatics author partners to support them in developing algorithms and insights on the Apervita platform. The ISE&amp;nbsp;will train, coach, and provide support to informatics authors during their algorithm development and testing.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;The primary language within the Apervita Author Editor will be Python, but the ISE will also be exposed to various data formats, ontologies, and other information related to the medical informatics used in the industry.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Additionally, the ISE will help create and maintain the public knowledgebase and documentation for the Apervita Author environment and will engage with the Development and QA teams in the improvement and testing of the platform.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;Apervita strives to enable developers to spend as much time as possible on design collaboration and development, running a low-overhead methodology based on Agile and Continuous Integration principles. We want developers to do what they do best, rising to meet technology challenges and working together as a team to build a world class platform.&lt;/span&gt;&lt;/p&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/87166/informatics-support-engineer-apervita-inc', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1338);
