INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1345, 'Software Engineer  - Java at Tactical Communication Group, LLC (Boston, MA)', '&lt;p&gt;&lt;strong&gt;Software Engineer &amp;ndash; Java &amp;ndash; C++&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Join a Fascinating Communications Company&lt;/strong&gt;&lt;br&gt;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We, &lt;strong&gt;Tactical Communications Group LLC&lt;/strong&gt;, have the opportunity for you to work for a leader in the Military Communications Market. In this position, you will serve as a Software Engineer working on cutting edge technology developing technologically advanced software applications and systems.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;You will be working with a collegial group of professionals who are serious about providing the best systems possible for their clients. &lt;strong&gt;This is a career move, not a job move.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Essence of the Position &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You, as the successful Software Engineer candidate, will have Java and/or C++ skills and object-oriented design and programming experience, ideally for military systems and clients. Also, you will have worked in a Windows environment. Beyond this (although not necessary), if you have GUI or TCP/IP or socket experience (see below) this is also of interest to us.&lt;/p&gt;', 'Boston, MA', 'http://careers.stackoverflow.com/jobs/98377/software-engineer-java-tactical-communication-group', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 1345);
