INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1347, 'PHP Developer at WSM International (Saint Clair Shores, MI) (allows remote)', '&lt;p&gt;WSM is an established and rapidly growing technical services and solutions company, offering unlimited career potential for talented and motivated developers. We thrive in a casual, open, friendly and collaborative environment. Our work is rewarded with very good pay and an awesome benefits plan including health, term life, short and long term disability, 401k with company match (yeah!), dental, vision and flexible PTO. This is a full-time, 40 hour/week position.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We''re looking for a sharp, motivated PHP Developer. You''ll create and implement a dynamic range of new web applications, and also maintain and develop a variety of existing applications.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop in PHP, JavaScript, MySQL and HTML&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with team members as well as operate individually to develop complex web-based applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain existing codebases, to include troubleshooting bugs and adding new features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gain an in-depth understanding of our customer''s business goals and related system interactions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with others to define and clarify project plans, deliverables, expectations, scheduling and of course, functionality of applications&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Saint Clair Shores, MI', 'http://careers.stackoverflow.com/jobs/82159/php-developer-wsm-international', 'Tue, 06 Oct 2015 15:02:02 Z', '2015-10-06T15:02:02Z', 597);
