INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1361, 'Core Developer; large-scale analytics solutions at 1010data (New York, NY)', '&lt;p&gt;We are seeking a Core Developer to design and implement large-scale analytics solutions; including building tools and interfaces used both internally and by customers, high performance data manipulation algorithms, and innovative ways to visualize and deliver result sets to customers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our Core team builds portable code that enables our customers to use 1010data&amp;rsquo;s system in a more autonomous way while maintaining performance when dealing with massive amounts of data.&amp;nbsp; They work directly with our customers for tangible feedback on products/technology, enabling rapid release cycles.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you appreciate the end-end ownership of building tools from scratch and are confident embracing new technologies to make a tangible impact come evolve our product as we continue to change the rules of big data discovery.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and implement parallel processing systems that operate on multiple terabytes of data&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build, tune and manage cloud based applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create technology solutions which have critical business impact&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'New York, NY', 'http://careers.stackoverflow.com/jobs/94901/core-developer-large-scale-analytics-solutions-1010data', 'Tue, 06 Oct 2015 14:02:02 Z', '2015-10-06T14:02:02Z', 1361);
