INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1363, 'IT Recruiter/Talent Acquisition Specialist at Docler Holding Luxembourg (Luxembourg City, Luxembourg)', '&lt;p&gt;Joining our Human Resources team, you will be in charge of the headhunting/recruiting for all of our open positions, focusing on senior IT profiles.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Key Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Keep in close contact with different departments in order to understand their recruitment needs and job role requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop an appropriate sourcing strategy according to the needs, including the use of social media to build and maintain a network of potential candidates&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Searching for skilled professionals, mainly IT profiles, developers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Providing top quality candidates&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Internal &amp;amp; external job postings, resume review and sourcing, candidate screening, scheduling interviews, composing offer letters.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Establish reports, dashboards and benchmarks with the aim of monitoring our activity and continually improving our processes&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Being a contact person for recruitment agencies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Attending career and professional events, conferences in order to attract top talents&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Building a network and creating a talent pool for our recruitment needs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Continuous improvement of employer branding&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participating in various projects in the areas of recruitment, sourcing.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we offer&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Competitive salary&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Attractive benefit package&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Interesting and challenging work&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Rewarding career thanks to our career management system&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Possibility to visit or present at conferences&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Opportunities to develop your professional and language skills and gain valuable practical experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Corporate events and team building activities&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A great, supportive, friendly and dynamic working environment&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Luxembourg City, Luxembourg', 'http://careers.stackoverflow.com/jobs/99632/it-recruiter-talent-acquisition-specialist-docler-holding-luxembourg', 'Tue, 06 Oct 2015 14:02:02 Z', '2015-10-06T14:02:02Z', 1356);
