INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1375, 'Ingnieur R&amp;D - Dveloppeur web full-stack at LUCCA - Editeur de logiciels (Paris, France)', '&lt;p&gt;Lucca est un &amp;eacute;diteur de logiciel&amp;nbsp;web,&amp;nbsp;en croissance forte et d&amp;rsquo;une quarantaine de collaborateurs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;L&amp;rsquo;entreprise compte plus de 800 clients parmi lesquels Deezer, Cheerz, l&amp;rsquo;Or&amp;eacute;al, Withings, M6, Price Minister, NRJ, Endemol, Pernod Ricard, ...&lt;/p&gt;&lt;br /&gt;&lt;p&gt;L&amp;rsquo;entreprise a un fonctionnement tr&amp;egrave;s participatif laissant une place importante &amp;agrave; l''initiative, &amp;agrave; l''innovation et au non-conformisme.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Nous recherchons un d&amp;eacute;veloppeur web full-stack pour compl&amp;eacute;ter l''&amp;eacute;quipe R&amp;amp;D.&lt;/p&gt;', 'Paris, France', 'http://careers.stackoverflow.com/jobs/99626/ing%C3%A9nieur-r-d-d%C3%A9veloppeur-web-full-stack-lucca-editeur-de-logiciels', 'Tue, 06 Oct 2015 14:02:02 Z', '2015-10-06T14:02:02Z', 1375);
