INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1382, 'Data Processing Engineer at Criteo (Paris, France)', '&lt;p&gt;&lt;strong&gt;Your Mission:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Build the appropriate infrastructure to store more than tens of TB of additional data every day, and offer both batch and nearline processing capabilities, at a rate of 300,000 messages per second per datacenter, across six datacenters.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Fully automate the administration of thousands of servers with a fully open source approach using Chef.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Datacenter as a computer: efficient usage of all hardware with a common storage and computation platform using Yarn / Mesos.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;High stakes, high rewards: 1% increase in performance may yield millions for the company.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Develop open source projects. Because we are working at the forefront of technology, we are dealing with problems that few have faced. We&amp;rsquo;re big users of open source, and we&amp;rsquo;d like to give back to the community.&lt;/p&gt;', 'Paris, France', 'http://careers.stackoverflow.com/jobs/84795/data-processing-engineer-criteo', 'Tue, 06 Oct 2015 13:02:02 Z', '2015-10-06T13:02:02Z', 1382);
