INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1384, 'Full-Stack Developer at Anders Innovations Oy (Helsinki, Finland)', '&lt;p&gt;Our strongly growing company is looking for experienced Web Developers. There are open positions located in Turku and Helsinki. If you are a motivated, spontaneous and determined full-stack programmer, we have the perfect job for you in Finland&amp;rsquo;s most competent software house.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;WE OFFER:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Interesting and motivating work environment at the best work place in Finland&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Career development program as a top-notch programmer with some of the most competent professionals in Finland&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participation in building a successful company&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Challenging and interesting software projects, not just tedious routine work&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Comprehensive possibilities for remote work&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Helsinki, Finland', 'http://careers.stackoverflow.com/jobs/94890/full-stack-developer-anders-innovations-oy', 'Tue, 06 Oct 2015 12:02:02 Z', '2015-10-06T12:02:02Z', 1384);
