INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1394, 'Graduates - Communications Technology Analysts at GCHQ (Manchester, UK)', '&lt;p&gt;Cheltenham, Manchester, Bude, Scarborough and Harrogate&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Competitive package based on qualifications and experience&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;GCHQ plays a major role in keeping the UK&amp;rsquo;s cyber connections and infrastructure safe and secure. You can imagine how valuable a new generation of technical graduates will be to our core capabilities. Join us and you can expect the highest quality technical training and development opportunities. The kind that will turn you into an experienced, highly qualified and dynamic leader in this field.&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Our&lt;strong&gt; Communications Technology Analysts &lt;/strong&gt;work in our Intelligence Operations teams and use data, technology and scientific approaches to solve problems. For instance, we&amp;rsquo;ll analyse the communication behaviours of our adversaries (and the technologies they use) or develop analytical tools to locate terrorists or investigate secure systems overseas.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Some of our projects involve a deep understanding of networks and protocols; in others we&amp;rsquo;ll use big data technologies and data mining to influence operational decision-making. Whichever projects you&amp;rsquo;re involved in, you&amp;rsquo;ll receive the training you need to build on your 2:2 degree in a relevant STEM subject, gained in 2015 or 2016.&lt;/p&gt;', 'Manchester, UK', 'http://careers.stackoverflow.com/jobs/99609/graduates-communications-technology-analysts-gchq', 'Tue, 06 Oct 2015 11:02:02 Z', '2015-10-06T11:02:02Z', 1394);
