INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1401, 'Front-End Javascript / AngularJS Developer at Rebel Minds (London, UK)', '&lt;p&gt;We''re looking for front-end developers who love building things in HTML, CSS and Javascript. You''ll work closely with our design and back-end teams creating beautiful and powerful products&amp;nbsp;for both small and enterprise projects.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our front-end stack includes:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;AngularJS&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gulp&lt;/li&gt;&lt;br /&gt;&lt;li&gt;SASS&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;We make digital products for clients, including mobile applications, back-end enterprise platforms and sexy consumer-facing websites.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Alongside longer-term partners, we also work on smaller 1-6 week&amp;nbsp;projects and these are typically handled by one or two people.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You''d be based in our central London offices.&amp;nbsp;We launch things fast, and build beautiful products with solid technical foundations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You should be a good communicator and be as open to working in a team as working alone.&amp;nbsp;We''re a very agile and fast-paced team with complimenting skill-sets, and you should be open to helping others and expanding your skills.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We focus on raw talent and aren''t too concerned about lots of work or education history for this role, but we would like to see some code you''ve written and your work.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;No recruiters please.&lt;/p&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/89835/front-end-javascript-angularjs-developer-rebel-minds', 'Tue, 06 Oct 2015 10:02:02 Z', '2015-10-06T10:02:02Z', 1401);
