INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1403, 'Senior Java/Mobile Developer at Nuroa Internet, SL (Barcelona, Spain)', '&lt;p&gt;We are looking for Java developers with android / ios&amp;nbsp;experience&amp;nbsp;for our development&amp;nbsp;team.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You would be part of the development&amp;nbsp;team, where you will be working with our product manager and designers, as well as with all the other techies, to implement new features in our website and mobile applications. Also, you will be fixing bugs, writing tests for your code, writing some scripts, ... You will be spending your time mostly with Java (Frontend and Backend stack built on top of Spring), Swift &amp;nbsp;and Android. We use agile development approach, continuous delivery and generally pretty neat.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;At all times, you will be working with a high degree of independence and responsibility.&lt;/p&gt;', 'Barcelona, Spain', 'http://careers.stackoverflow.com/jobs/99603/senior-java-mobile-developer-nuroa-internet-sl', 'Tue, 06 Oct 2015 10:02:02 Z', '2015-10-06T10:02:02Z', 1403);
