INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1407, 'Network Defence Researchers at GCHQ (Cheltenham, UK)', '&lt;p&gt;&lt;strong&gt;Cyber Security and Information Assurance&lt;br&gt; Competitive package based on qualifications and experience &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Defending against emerging threats.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is your chance to join a team of cyber security researchers, engineers and consultants skilled in a wide range of technologies and working on the hardest assurance problems faced by government. Our team members work with experts from across GCHQ and its partners to understand the technologies being examined and give the best possible advice to our customers in industry and the public sector.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will conduct research to gain a sound understanding of areas such as platform security, mobile technology, vulnerabilities, security, cryptography applications and processor technologies. Part of the UK National Technical Authority for Cyber Security and Information Assurance, you will play a key role in defending the UK against emerging threats and building expertise.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You&amp;rsquo;ll come to us with a sound understanding of cyber security across government and industry together with experience of delivering bespoke identity assurance services that meet our customers&amp;rsquo; needs. An excellent communicator, skilled in collaborating in a team environment, you will be committed to your personal and professional development and the development of others. Above all, you&amp;rsquo;ll have a strong interest in the latest technologies.&lt;/p&gt;', 'Cheltenham, UK', 'http://careers.stackoverflow.com/jobs/99588/network-defence-researchers-gchq', 'Tue, 06 Oct 2015 10:02:02 Z', '2015-10-06T10:02:02Z', 1394);
INSERT INTO PROPOSES(CompanyId, OfferId) VALUES (1394, 1407);
INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1408, 'Security and Information Risk Management Advisors at GCHQ (Cheltenham, UK)', '&lt;p&gt;&lt;strong&gt;Cyber Security and Information Assurance&lt;br&gt; Competitive package based on qualifications and experience &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Minimise the risk to critical assets.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our Information Risk Management Teams are specialists with broad information assurance and technical knowledge. We need capable communicators to join us who can offer guidance to our wide range of customers, from GCHQ to critical national infrastructure and the UK Government.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will provide advice and guidance on risk management to customers across UK government and critical national infrastructure while working closely with technical experts to deliver risk management solutions. The role will also see you take responsibility for all aspects of the lifecycle of national information assurance policy and risk management governance as it is applied to the government, the wider public sector and supply chain. You&amp;rsquo;ll have the chance to benefit from a tailored development programme to ensure you remain at the forefront of your field.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Known for your expertise in risk management, you will bring a sound awareness of cyber security across government and industry together with a strong interest in the latest technology, particularly security technology. Skilled in delivering bespoke services with an understanding of the needs of customers, you will have a flair for collaboration. You&amp;rsquo;ll also be keen to take advantage of the excellent development opportunities within the team.&lt;/p&gt;', 'Cheltenham, UK', 'http://careers.stackoverflow.com/jobs/99589/security-and-information-risk-management-advisors-gchq', 'Tue, 06 Oct 2015 10:02:02 Z', '2015-10-06T10:02:02Z', 1394);
INSERT INTO PROPOSES(CompanyId, OfferId) VALUES (1394, 1408);
INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1409, 'Information Assurance Architects at GCHQ (Cheltenham, UK)', '&lt;p&gt;&lt;strong&gt;Cyber Security and Information Assurance&lt;br&gt; Competitive package based on qualifications and experience &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Build security in from the beginning.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is your chance to join a team of cyber security researchers, engineers and consultants skilled in a wide range of technologies and working on the hardest assurance problems faced by government. Our team members work with experts from across GCHQ and its partners to understand the technologies being examined and give the best possible advice to our customers in industry and the public sector.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an Information Assurance Architect, you will provide technical architectural guidance to GCHQ and the government. You will focus on engaging customers early in the design process and use your deep technical knowledge and experience of other systems to build in security from the outset and avoid potential problems and expense further down the line. It&amp;rsquo;s a fast-moving field, so you&amp;rsquo;ll benefit from our tailored development programme to remain at the forefront of your field.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You&amp;rsquo;ll come to us with a sound understanding of cyber security across government and industry together with experience of delivering bespoke identity assurance services that meet our customers&amp;rsquo; needs. An excellent communicator, skilled in collaborating in a team environment, you will be committed to your personal and professional development and the development of others. Above all, you&amp;rsquo;ll have a strong interest in the latest technologies.&lt;/p&gt;', 'Cheltenham, UK', 'http://careers.stackoverflow.com/jobs/99590/information-assurance-architects-gchq', 'Tue, 06 Oct 2015 10:02:02 Z', '2015-10-06T10:02:02Z', 1394);
INSERT INTO PROPOSES(CompanyId, OfferId) VALUES (1394, 1409);
INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1410, 'Identity Assurance Researchers and Consultants at GCHQ (Cheltenham, UK)', '&lt;p&gt;&lt;strong&gt;Cyber Security and Information Assurance&lt;br&gt; Competitive package based on qualifications and experience &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Make a smart move.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is your chance to join a team of cyber security researchers, engineers and consultants skilled in a wide range of technologies and working on the hardest assurance problems faced by government. Our team members work with experts from across GCHQ and its partners to understand the technologies being examined and give the best possible advice to our customers in industry and the public sector.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Take this key role, and you will provide advice to government on the use of identity technologies, such as biometrics and smart cards. It&amp;rsquo;s an excellent opportunity to prove your sound understanding of hardware and software, strong analytical skills and in-depth understanding of how identity technologies work.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You&amp;rsquo;ll come to us with a sound understanding of cyber security across government and industry together with experience of delivering bespoke identity assurance services that meet our customers&amp;rsquo; needs. An excellent communicator, skilled in collaborating in a team environment, you will be committed to your personal and professional development and the development of others. Above all, you&amp;rsquo;ll have a strong interest in the latest technologies.&lt;/p&gt;', 'Cheltenham, UK', 'http://careers.stackoverflow.com/jobs/99591/identity-assurance-researchers-and-consultants-gchq', 'Tue, 06 Oct 2015 10:02:02 Z', '2015-10-06T10:02:02Z', 1394);
