INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1432, 'PHP Backend Developer-Dubai at Propertyfinder (Dubai, United Arab Emirates)', '&lt;p&gt;We are looking for an incredibly talented developer to join our IT team here at Propertyfinder. We believe in nurturing our talent and we encourage creativity and autonomy, where you are free to grow and develop your career in a fun, forward thinking and positive environment.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Tasks, responsibilities and qualities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;In a 20 persons IT department, divided in smaller teams, you&amp;rsquo;ll be in charge of developing new features, as well as refactoring existing projects.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You need to be able to write clean and scalable code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You&amp;rsquo;ll need to be force of proposal during the brain stormings, to bring your opinion and experience acquired in your previous experiences.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Be able to deliver features, from 0% to 100%, tested (manual and automated), efficient, and working.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Being performance oriented, we test the performances of our queries, our PHP pages, our CRONs.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Being passionate, interested in new technologies, the future version of PHP, its new features and cool stuff. We use all the latest and cool technologies, Zend 2, ElasticSearch, MongoDb, NodeJS.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Dubai, United Arab Emirates', 'http://careers.stackoverflow.com/jobs/99582/php-backend-developer-dubai-propertyfinder', 'Tue, 06 Oct 2015 09:02:02 Z', '2015-10-06T09:02:02Z', 1085);
