INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1439, 'Java-utvecklare at Etimo (Stockholm, Sweden)', '&lt;p&gt;Etimo hj&amp;auml;lper ledande organisationer inom Telekom, Spel, Media, Finans, E-handel och IT att utveckla aff&amp;auml;rskritiska system. D&amp;auml;rmed st&amp;auml;lls h&amp;ouml;ga krav, b&amp;aring;de p&amp;aring; oss som individer och p&amp;aring; de l&amp;ouml;sningar vi utvecklar. Ett typiskt projekt &amp;auml;r mellan tre och nio m&amp;aring;nader l&amp;aring;ngt d&amp;auml;r vi arbetar tillsammans med klienten fr&amp;aring;n A till &amp;Ouml; f&amp;ouml;r att utveckla en l&amp;ouml;sning p&amp;aring; ett aff&amp;auml;rskritiskt problem.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;V&amp;aring;r filosofi: M&amp;auml;nniskorna &amp;auml;r allt&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Medarbetarna &amp;auml;r v&amp;aring;r h&amp;ouml;gsta prioritet och vi satsar p&amp;aring; att bli b&amp;auml;st. Utg&amp;aring;ngspunkten i detta arbete &amp;auml;r kompetens. Etimo &amp;auml;r ett kompetenscentrum inom IT och utveckling. Vi skapar kundv&amp;auml;rde med hj&amp;auml;lp av teknisk spetskompetens, djup insikt i utvecklingsprocesser och enast&amp;aring;ende f&amp;ouml;rm&amp;aring;ga att l&amp;ouml;sa problem. Dessa verktyg &amp;auml;r fundamentala f&amp;ouml;r oss.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Med tanke p&amp;aring; den snabba teknikutvecklingen i v&amp;auml;rlden &amp;auml;r vi &amp;ouml;vertygade om att dagens kompetens inte r&amp;auml;cker f&amp;ouml;r att l&amp;ouml;sa morgondagens problem. St&amp;auml;ndig f&amp;ouml;rb&amp;auml;ttring &amp;auml;r d&amp;auml;rmed ett krav f&amp;ouml;r att f&amp;ouml;rbli relevant och vi l&amp;auml;gger betydande kraft p&amp;aring; att l&amp;auml;ra mer, l&amp;auml;ra nytt och bli b&amp;auml;ttre, b&amp;aring;de som individer och som team. Genom en icke-hierarkisk struktur och ett prestigel&amp;ouml;st f&amp;ouml;rh&amp;aring;llningss&amp;auml;tt uppmuntrar vi nya id&amp;eacute;er och f&amp;ouml;rb&amp;auml;ttringsf&amp;ouml;rslag fr&amp;aring;n hela organisationen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Samarbete m&amp;ouml;jligg&amp;ouml;r kompetensdelning och d&amp;auml;rmed st&amp;auml;ndig f&amp;ouml;rb&amp;auml;ttring. En stark grupp som arbetar tillsammans n&amp;aring;r h&amp;ouml;gre &amp;auml;n den b&amp;auml;sta individ. D&amp;auml;rf&amp;ouml;r &amp;auml;r samarbete centralt f&amp;ouml;r oss. Dessutom har vi roligare p&amp;aring; v&amp;auml;gen!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Sist men inte minst kr&amp;auml;vs alltid fokus p&amp;aring; aff&amp;auml;rsproblemet. Det &amp;auml;r endast genom f&amp;ouml;rst&amp;aring;else f&amp;ouml;r kundens utmaningar och k&amp;auml;rnaff&amp;auml;r som vi kan leverera best&amp;aring;ende kundv&amp;auml;rde. Med hj&amp;auml;lp av teknik och genom ett n&amp;auml;ra samarbete tar vi oss an och l&amp;ouml;ser kundens problem. Baserat p&amp;aring; kundens situation och problemets natur skr&amp;auml;ddarsyr vi l&amp;ouml;sningen f&amp;ouml;r att skapa f&amp;ouml;rstklassigt kundv&amp;auml;rde.&lt;/p&gt;', 'Stockholm, Sweden', 'http://careers.stackoverflow.com/jobs/89897/java-utvecklare-etimo', 'Tue, 06 Oct 2015 08:02:02 Z', '2015-10-06T08:02:02Z', 1439);
