INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1444, '.NET Developer at Nmbrs (Amsterdam, Netherlands)', '&lt;p&gt;&lt;strong&gt;The Team&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The development team is multidisciplinary, consists of enthusiasts from various countries with different functions: programmers, analysts, designers and product engineers. The team is global located: in Amsterdam and Lisbon. This job opening is in Amsterdam.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We work in Microsoft .NET environment (ASP.NET), MVC, jQuery, MS SQL Server, Visual Studio, and use a variety of planning tools, verification, documentation and code optimization.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;The Squad&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Together with one Functional Analyst, one Tester, a few Customer Supporters and other Developers you are responsible for the development, bug solving and new features for a few of the modules in our application. Since you work closely together with colleagues from other disciplines you are going to be involved and engaged to more than just the developing part.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;The .NET Developer&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;You will participate in drafting tasks and implementation of solutions;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You analyze software, requirements, code and performance;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Implement development tasks (implementation and maintenance solutions) and direct responsibility for the quality and development testing.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Amsterdam, Netherlands', 'http://careers.stackoverflow.com/jobs/99579/net-developer-nmbrs', 'Tue, 06 Oct 2015 07:02:02 Z', '2015-10-06T07:02:02Z', 1444);
