INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1447, 'Quality assurance at MyQ (Prague, Czech Republic)', '&lt;p&gt;We are looking for applicants for the position of Quality Assurance Officer. The position will consists of assuring quality of the MyQ system and preparing test scenarios. The officer will work in close cooperation with our software developers, support department and document administration department. The tested products are developed mainly for the following environments: OS Windows / Windows server, mobile devices (Android, iOS), printers and multifunctional devices of various brands, MyQ own HW.&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your place of work will be Prague, The Czech Republic.&lt;/p&gt;', 'Prague, Czech Republic', 'http://careers.stackoverflow.com/jobs/94866/quality-assurance-myq', 'Tue, 06 Oct 2015 07:02:02 Z', '2015-10-06T07:02:02Z', 1447);
