INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1454, 'Linux Engineer at Avvo, Inc. (Seattle, WA)', '&lt;p&gt;Avvo is expanding its Dev Ops team to support the growing infrastructure of our organization! The Linux DevOps Engineer will focus on enhancing the performance and reliability of our services with exceptional knowledge of web-service scaling best practices. Our ideal candidate has strong working knowledge of MySQL, particularly replication and high availability configuration. This position will report to the DevOps Lead and will be secondary on-call escalation, supporting the primary on-call with more severe issues.&lt;/p&gt;', 'Seattle, WA', 'http://careers.stackoverflow.com/jobs/64792/linux-engineer-avvo-inc', 'Tue, 06 Oct 2015 05:02:02 Z', '2015-10-06T05:02:02Z', 1454);
