INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1456, 'API/Backend Developer at Avvo, Inc. (Seattle, WA)', '&lt;p&gt;We&amp;rsquo;re looking for great software developers to help us connect people with the legal help they need.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an API / Backend Developer, you&amp;rsquo;ll build fast, easy-to-use APIs to help our Mobile team ship remarkable apps. You&amp;rsquo;ll bridge between our iOS, web, and platform teams, and help us unlock new ways to bring our data to other apps and devices.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;About the Team:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;We mostly run on Rails, but have brought in technologies like Go, Angular.js, Scala, and Sinatra. We&amp;rsquo;re open to using the right technologies for what we build.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We iterate quickly, and ship multiple times a day.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We work in small teams with product managers and designers, and help shape the products we&amp;rsquo;ll be building from day 1.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We have a strong tradition of supporting open source. Projects like &lt;a href="https://github.com/resque/resque-scheduler" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;resque_scheduler&lt;/a&gt;, &lt;a href="https://github.com/justinweiss/robut" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;robut&lt;/a&gt;, &lt;a href="https://github.com/justinweiss/resque_unit" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;resque_unit&lt;/a&gt;, and &lt;a href="https://github.com/chingor13/json_api_client" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;json&lt;em&gt;api&lt;/em&gt;client&lt;/a&gt; were all extracted from our codebase, and we&amp;rsquo;ve contributed several patches back to Rails.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Why Avvo?&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Right now, you have an opportunity to join the team while it&amp;rsquo;s still on the small side, and grow your career faster than you would at almost any other company. You&amp;rsquo;ll be able to help shape our dev organization and define our culture.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Companies in our position are rare, and joining a company like ours will be one of the most memorable parts of your life.&lt;/p&gt;', 'Seattle, WA', 'http://careers.stackoverflow.com/jobs/93317/api-backend-developer-avvo-inc', 'Tue, 06 Oct 2015 05:02:02 Z', '2015-10-06T05:02:02Z', 1454);
