INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1457, 'UI Developer at Avvo, Inc. (Seattle, WA)', '&lt;p&gt;We&amp;rsquo;re in the process of rebranding and redesigning our entire site and are looking for a front-end developer to help us. We&amp;rsquo;re constructing a new UI framework from the ground up&amp;mdash;and you&amp;rsquo;ll be one of its chief architects.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You&amp;rsquo;ll work closely with our design team and other engineers as we develop new web components for our site using HTML, CSS, and JS. You&amp;rsquo;ll face challenges in maximizing each component&amp;rsquo;s flexibility and versatility, while also considering established patterns and impacts to other components in the larger framework.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Help visual and UX designers with the technical considerations of new designs.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build reusable components using HTML, SASS, and JS, focusing on flexibility and maintainability.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Document these components and work with full-stack engineers to get them on our website.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Seattle, WA', 'http://careers.stackoverflow.com/jobs/76292/ui-developer-avvo-inc', 'Tue, 06 Oct 2015 05:02:02 Z', '2015-10-06T05:02:02Z', 1454);
